define('text!views/popups/enterpriseHelpPopup.html',[],function () { return '<div class="help-popup">\r\n    <span class="help-popup-icon" data-bind="visible: showHelpPopupButton(), click: showHelpPopup"></span>\r\n\r\n    <div class="help-popup-overlay" data-bind="visible: helpPopupVisible(), click: hideHelpPopup"></div>\r\n\r\n    <div class="help-popup-dialog" data-bind="visible: helpPopupVisible()">\r\n        <h1 class="help-popup-header" data-bind="local: \'shell.help\'"></h1>\r\n        <i class="help-popup-close" data-bind="click: hideHelpPopup"></i>\r\n        <section class="help-popup-content">\r\n\r\n            <h3>General Tab</h3>\r\n            <dl>\r\n                <dt>Stores Business</dt>\r\n                <dd>The type of business the store belongs to.</dd>\r\n\r\n                <dt>External Payment</dt>\r\n                <dd>Allows you to use an external terminal for card payments.</dd>\r\n\r\n                <dt>Item Notes</dt>\r\n                <dd>Select whether to now allow notes on items, make it a mandatory step, or an optional step.</dd>\r\n\r\n                <dt>Auto-Logout After Each Order</dt>\r\n                <dd>Select to log out of the system after an order is placed.</dd>\r\n\r\n                <dt>Synchronization on Login</dt>\r\n                <dd>Automatically synchronize all POS data once you log in to the system.</dd>\r\n\r\n                <dt>Open Cash Drawer on Credit/Debit Card Transaction</dt>\r\n                <dd>Automatically opens the register when you process a transaction using a credit/debit card.</dd>\r\n\r\n                <dt>Show Tax List</dt>\r\n                <dd>Display a list of taxes for items as they are added to the cart.</dd>\r\n            </dl>\r\n\r\n            <h3>Contacts</h3>\r\n            <dl>\r\n                <dt>Enterprise Code</dt>\r\n                <dd>Your eHopper Account ID.</dd>\r\n\r\n                <dt>Enterprise Name</dt>\r\n                <dd>Your business name.</dd>\r\n\r\n                <dt>Address 1/2</dt>\r\n                <dd>Your businesses street address.</dd>\r\n\r\n                <dt>City</dt>\r\n                <dd>The city where your business is located.</dd>\r\n\r\n                <dt>State</dt>\r\n                <dd>The state where your business is located within the USA.</dd>\r\n\r\n                <dt>Zip</dt>\r\n                <dd>The zip code of your business when located in the USA.</dd>\r\n\r\n                <dt>Country</dt>\r\n                <dd>The country your business is located in.</dd>\r\n\r\n                <dt>Phone</dt>\r\n                <dd>Your business phone number.</dd>\r\n\r\n                <dt>Email</dt>\r\n                <dd>Your email address linked to the buinsess.</dd>\r\n\r\n                <dt>Web</dt>\r\n                <dd>The web address of your businesses website.</dd>\r\n            </dl>\r\n\r\n            <h3>Receipt Tab</h3>\r\n            <dl>\r\n                <dt>Kitchen Receipt</dt>\r\n                <dd>Select if you have a second printer for your kitchen and wish to send a customized receipt to it.</dd>\r\n\r\n                <dt>Footer</dt>\r\n                <dd>Enter the text what will be printed on the kitchen receipt that will be removed from the customer receipt.</dd>\r\n            </dl>\r\n\r\n            <h3>Tips Tab</h3>\r\n            <dl>\r\n                <dt>Tips</dt>\r\n                <dd>Select to enable the tips function.</dd>\r\n\r\n                <dt>Tips on Credit Card Authorization</dt>\r\n                <dd>Automatically adds a calculated amount for tipping to an order upon authorization.</dd>\r\n\r\n                <dt>Tips & Signature</dt>\r\n                <dd>Select to have tips and signatures taken either on screen, on receipt or on the terminal itself.</dd>\r\n\r\n                <dt>Tips & Signature on POS</dt>\r\n                <dd>Enable or disable taking tips and signatures in eHopper POS.</dd>\r\n\r\n                <dt>Keyboard Shortcut Settings</dt>\r\n                <dd>When using a tablet, set and use keyboard shortcuts for easier input.</dd>\r\n            </dl>\r\n\r\n            <h3>Taxes Tab</h3>\r\n            <p>Set the Tax you wish to use, as well as the rate and order.</p>\r\n\r\n            <h3>Localization</h3>\r\n            <dl>\r\n                <dt>Account Languages</dt>\r\n                <dd>Selects the language you wish to view eHopper in.</dd>\r\n\r\n                <dt>Default Language</dt>\r\n                <dd>Selects the language eHopper will use by default.</dd>\r\n\r\n                <dt>POS Language</dt>\r\n                <dd>Selects the language the Point Of Sale will be displayed in.</dd>\r\n\r\n                <dt>Kitchen Receipt Language</dt>\r\n                <dd>Selects the language that kitchen receipts will be printed in.</dd>\r\n\r\n                <dt>BO Language</dt>\r\n                <dd>Selects the language the Back Office will be displayed in.</dd>\r\n\r\n                <dt>Date Format</dt>\r\n                <dd>Selects in what order the day, month and year is displayed in eHopper.</dd>\r\n\r\n                <dt>Time Format</dt>\r\n                <dd>Selects in what order hours, minutes, seconds and 12-hour marker.</dd>\r\n\r\n                <dt>Currency</dt>\r\n                <dd>Selects the type of currency that is being used to handle cash transactions in eHopper.</dd>\r\n            </dl>\r\n\r\n        </section>\r\n    </div>\r\n</div>';});


